      <> <h1> FN:s Hållbarhetsmål </h1> <div className='goal'>{goalTitles.map(title => <Goal titleObj={title} key={title.code} showTitle={showTitle}/>)} </div>
      </>
        }    */}
      </section>
  );
}

export default App;
